function normalizeUrl(raw) {
  try {
    const u = new URL(String(raw || '').trim());
    u.hash = '';
    const dropKeys = new Set(['fbclid', 'gclid', 'yclid']);
    for (const key of Array.from(u.searchParams.keys())) {
      const k = key.toLowerCase();
      if (k.startsWith('utm_') || dropKeys.has(k)) u.searchParams.delete(key);
    }
    return u.toString();
  } catch (e) {
    return String(raw || '').trim();
  }
}

function stripTags(s) {
  return String(s || '')
    .replace(/<[^>]*>/g, ' ')
    .replace(/\s+/g, ' ')
    .trim();
}

function hostFromUrl(url) {
  try {
    return new URL(url).hostname.toLowerCase().replace(/^www\./, '');
  } catch {
    return null;
  }
}

const items = $input.all();
const out = [];

for (const it of items) {
  const j = it.json;

  const title = String(j.title || '').trim();
  const canonical_url = normalizeUrl(j.link || j.url || j.guid || '');
  if (!canonical_url) continue;

  // summary
  const summaryRaw = j.contentSnippet || j.content || j.summary || j.description || '';
  const rss_summary = stripTags(summaryRaw);

  // image
  const image_url = j.enclosure?.url || j['media:content']?.url || j.image?.url || null;

  // published
  const cand = j.isoDate || j.pubDate || j.published || j.date || '';
  const d = cand ? new Date(cand) : null;

  // Если дата невалидна, пропускаем. Если валидна — идем дальше.
  if (!d || Number.isNaN(d.getTime())) continue;

  // filter last week — ОТКЛЮЧЕНО
  // if (Date.now() - d.getTime() > WEEK_MS) continue;

  out.push({
    json: {
      title,
      canonical_url,
      source_name: hostFromUrl(canonical_url) || 'unknown',
      rss_summary,
      published_at: d.toISOString(),
      image_url,
    }
  });
}

return out;

function strip(s) {
