function absUrl(u) {
  const s = strip(u);
  if (!s) return null;
  if (s.startsWith("http://") || s.startsWith("https://")) return s;
  if (s.startsWith("//")) return "https:" + s;
  if (s.startsWith("/")) return "https://www.hunting.ru" + s;
  return "https://www.hunting.ru/" + s;
}

const RU_MONTHS = {
  "января":0,"февраля":1,"марта":2,"апреля":3,"мая":4,"июня":5,
  "июля":6,"августа":7,"сентября":8,"октября":9,"ноября":10,"декабря":11
};

function parseRuDate(s) {
  const t = strip(s).toLowerCase();
  if (!t) return null;
  const m = t.match(/(\d{1,2})\s+(января|февраля|марта|апреля|мая|июня|июля|августа|сентября|октября|ноября|декабря)\s+(\d{4})/);
  if (!m) return null;
  const d = new Date(Date.UTC(+m[3], RU_MONTHS[m[2]], +m[1]));
  return isNaN(d.getTime()) ? null : d.toISOString();
}

return $input.all().map((item) => {
  const j = item.json || {};
  return {
    json: {
      title: strip(j.title) || null,
      canonical_url: absUrl(j.canonical_url),
      source_name: "hunting.ru",
      rss_summary: strip(j.rss_summary) || null,
      published_at: parseRuDate(j.published_text),
      image_url: absUrl(j.image_url)
    }
  };
});

