// n8n Code node (JavaScript) - normalize RSS items

function stripHtml(html) {
  const s = String(html || "");
  return s
    .replace(/<script[\s\S]*?>[\s\S]*?<\/script>/gi, " ")
    .replace(/<style[\s\S]*?>[\s\S]*?<\/style>/gi, " ")
    .replace(/<[^>]+>/g, " ")
    .replace(/\s+/g, " ")
    .trim();
}

function decodeEntities(s) {
  return String(s || "")
    .replace(/&nbsp;/g, " ")
    .replace(/&amp;/g, "&")
    .replace(/&quot;/g, "\"")
    .replace(/&#39;/g, "'")
    .replace(/&lt;/g, "<")
    .replace(/&gt;/g, ">");
}

function toIsoDate(item) {
  if (item.isoDate) return new Date(item.isoDate).toISOString();
  if (item.pubDate) {
    const d = new Date(item.pubDate);
    if (!isNaN(d.getTime())) return d.toISOString();
  }
  return null;
}

function hostFromUrl(u) {
  try {
    return new URL(String(u)).host || null;
  } catch (e) {
    return null;
  }
}

return $input.all().map((it) => {
  const j = it.json || {};
  const title = decodeEntities(stripHtml(j.title));
  const url = String(j.link || "").trim() || null;

  const summaryRaw =
    j.contentSnippet ||
    j.content ||
    "";

  const rssSummary = decodeEntities(stripHtml(summaryRaw)) || null;

  const imageUrl =
    (j.enclosure && j.enclosure.url ? String(j.enclosure.url).trim() : null) || null;

  const publishedAt = toIsoDate(j);

  const categories = Array.isArray(j.categories) ? j.categories : [];

  return {
    json: {
      title: title || null,
      canonical_url: url,
      source_name: hostFromUrl(url) || "rhm-magazine.ru",
      rss_summary: rssSummary,
      published_at: publishedAt,
      image_url: imageUrl,
      categories
    }
  };
});

